from samson.prngs.flfsr import FLFSR
from samson.prngs.glfsr import GLFSR
from samson.utilities.bytes import Bytes
import unittest

# Test vectors generated manually using code -> https://en.wikipedia.org/wiki/Linear-feedback_shift_register#Fibonacci_LFSRs
class FFLSRTestCase(unittest.TestCase):
    def _run_test(self, seed, poly, expected_output):
        flfsr = FLFSR(seed, poly)
        self.assertEqual([flfsr.generate() for _ in range(len(expected_output))], expected_output)


